import { MachineConfig, send, assign } from "xstate";
import { listen, promptAndAsk, nluRequest, misUnderstood } from "./index";
// import { list } from "./todo_list"


export function Queries(ondone: string, onerror:string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
            invoke: {
                id: 'rasa',
                src: (context, event) => nluRequest(context.option),
                onDone: {
                    actions: [assign((context, event) => { return  {option: event.data.intent.name} }), 
                    (context: SDSContext, event: any) => console.log(event.data)],
                    target: ondone
                },
                onError: {
                    target: onerror,
                    actions: (context, event) => console.log(event.data)}
}})}

export function Conditional(cond1: string, target1: string, cond2: string, target2: string, saythis: string,): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
        initial: "prompt",
                on: {
                    ENDSPEECH: [
                            {cond: (context) => context.option === cond1, target: target1},
                            {cond: (context) => context.option === cond2, target: target2},
                            { target: "others" }] 

                    },
                states: {
                    prompt: {
                        entry: send(({type: "SPEAK", value: `Cool.`})),
                    }
                }
})}


export const dmMachine1: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        // ...
        idle: {},
        // ...
        positive: {
            initial: "quest1",
            states: {
                quest1: {
                        on: {
                            RECOGNISED: {
                                target: '#root.dm1.query1',
                                actions: assign((context) => { return { option: context.recResult } }),
                            }},
                        ...promptAndAsk("As you are in a good mood, I suppose you could do something productive. Do you agree?")
                        // NOTE: TEXT COULD BE IMPROVED
                    },
                    quest2: {
                        on: {
                            RECOGNISED: {
                                target: '#root.dm1.query3',
                                actions: assign((context) => { return { option: context.recResult } }),
                            }},
                        ...promptAndAsk("Then what would you like to do?")
                        // NOTE: TEXT COULD BE IMPROVED
                    }
            // ...misUnderstood(`Would you like to do a chore?`,  ["#root.dm1.idle", "#root.initial_welcome.help"])
             }},
            query1: {...Queries('conditional1', "#root.initial_welcome.help")},
            conditional1: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                            {cond: (context) => context.option === 'agreement', target: "create_do"},
                            {cond: (context) => context.option === 'disagreement', target: "if_ideas"},
                            { target: "others" }] 

                    },
                states: {
                    prompt: {
                        entry: send(({type: "SPEAK", value: `Cool.`})),
                    }
                }
            },
            // ....
            query3: {...Queries('distributor', "#root.initial_welcome.help")},
            distributor: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                            {cond: (context) => context.option === 'games', target: "leftovers"},
                            {cond: (context) => context.option === 'music', target: "leftovers"},
                            {cond: (context) => context.option === 'enterntain_me', target: "leftovers"},
                            { target: "others" }] 

                    },
                states: {
                    prompt: {
                        entry: send(({type: "SPEAK", value: `Ok.`})),
                    }
                }
            },
            // ...
            others: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                        '#root.dm1.idle']
                    },
                    states: {
                        prompt: {
                            entry: send(({
                                type: "SPEAK",
                                value:"Ummm, you said something strange" })),
                             on: { ENDSPEECH: '#root.dm1.positive.quest1'}
                            }
                        }
            },
            //...
            create_do:{
                initial: "prompt",
                on: {
                    RECOGNISED: {
                        target: "to_do",
                        actions: assign((context) => { return { task: context.recResult } }),
                    }},
                    ...promptAndAsk("Tell me the things you have to accomplish.")
                },
            // ...
            to_do: {
                initial: "prompt",
                on: {
                    ENDSPEECH: ['#root.dm1.idle']
                    },
                    states: {
                        prompt: {
                            entry: send((context) => ({
                                type: "SPEAK",
                                value:`Now go and complete the following tasks: ${context.task}` })),
                             on: { ENDSPEECH: '#root.dm1.done'}
                    }
                }
            },
            // ...
            if_ideas: {
                on: {
                    RECOGNISED: {
                        target: '#root.dm1.query2',
                        actions: assign((context) => { return { option: context.recResult } }),
                    }},
                ...promptAndAsk("Then how about working on your ideas?")
            //     on: {
            //         ENDSPEECH: [
            //             {cond: (context) => context.option === 'agreement', target: "create_ideas"},
            //             {cond: (context) => context.option === 'disagreement', target: "quest2"},
            //             // { target: "others" }] 
            //         ]
