                        // {cond: (context) => context.option === 'negative', target: ["#root.dm2.denial", "idle"]},
                        {cond: (context) => context.option === 'help', target: "help"},

                        {target: ".goodbye"} ]
                        },
                        ...promptHelpBye("I suppose you could do something productive. Do you agree?", '#root.dm2.idle')
                        },
