import { MachineConfig, send, assign } from "xstate";
import {say, listen, promptAndAsk } from "./index";

// import { nluRequest } from './index'
// import { Endings } from './index'

const url_grammar: { [index: string]: {url:  string } } = 
         {  "rock": { url: 'asd' },
            "metal": { url: 'asd' },
            "punk": { url: 'asd' },
            "rap": { url: 'asd' },
            "lo-fi": { url: 'asd' },
            "house": { url: 'asd' },
            "techno": { url: 'asd' },
            "country": { url: 'asd' },
}

// const openInNewTab: any(user_input: string) {
//   window.open(url_grammar[user_input]),
// }

function openInNewTab(user_input: string): any{ 
  return (window.open(url_grammar[user_input].url, '_blank'))
}


// function openInNewTab(input_user: string) => void {
//     // let context = url;
//     // let newRelativeUrl = router.createUrlTree([namedRoute]);
//     // let baseUrl = window.location.href.replace(router.url, '');

//     window.open(baseUrl + newRelativeUrl, '_blank');
// }


export const dmMachine2: MachineConfig<SDSContext, any, SDSEvent> = ({
  initial: 'negative',
  states: {
      // ...
      negative: {
        initial: "first_prompt",
        // ...
        on: { ENDSPEECH: "ask_genre" },
            states: { 
                first_prompt: { entry: say("Let's listen to some music!") },
              }
            },
      ask_genre: {
        initial: "second_prompt",
        on: {
          RECOGNISED: [{
              // cond: (context) => grammar[context.recResult].person !== undefined,
              cond: (context) => "genre" in (url_grammar[context.recResult] || {}), // It checks if url is in grammar
              actions: assign((context) => { return { url: url_grammar[context.recResult].url } }), // The computer keeps the information in the object context
              target: "genre"
          },
          { target: ".nomatch" }]
      },
      states: {
        second_prompt: {
          entry: say('What genre would you like to listen to?'),
          on: { ENDSPEECH: "ask" }
        },
        ask: {
            entry: listen()
          },
        nomatch: {
          entry: say('I am sorry, I cannot play that genre for you. Please choose another genre'),
          on: { ENDSPEECH: "ask"}
        }
      }
      },
      games: {},
      entertain: {},
      genre: {
        initial: 'third_prompt',
        states: {
          third_prompt: {
            entry: send((context) => ({ // We need to access the context that has the info. Similar to "say" function
                      ...openInNewTab(context.url)
                        // type: "SPEAK",
                        // value: `OK. Do you want to listen to $(context.genre)?`
                    })),
          }
        }
      }
      }})
