import { MachineConfig, send, assign } from "xstate";
import {say, listen, promptAndAsk, nluRequest, misUnderstood } from "./index";
import { list } from "./todo_list"

export function Queries(ondone: string, onerror:string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
            invoke: {
                id: 'rasa',
                src: (context, event) => nluRequest(context.option),
                onDone: {
                    actions: [assign((context, event) => { return  {option: event.data.intent.name} }), 
                    (context: SDSContext, event: any) => console.log(event.data)],
                    target: ondone
                },
                onError: {
                    target: onerror,
                    actions: (context, event) => console.log(event.data)}
}})}

export const dmMachine1: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        // ...
        idle: {},
        // ...
        positive: {
            initial: "quest",
            states: {
                quest: {
                        on: {
                            RECOGNISED: {
                                target: '#root.dm1.query1',
                                actions: assign((context) => { return { option: context.recResult } }),
                            }},
                        ...promptAndAsk("Would you like to do a chore?")
                    }
            // ...misUnderstood(`Would you like to do a chore?`,  ["#root.dm1.idle", "#root.initial_welcome.help"])
        }},
            query1: { 
                ...Queries('distributor1', "#root.initial_welcome.help")
            },
            distributor1: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                            {cond: (context) => context.option === 'agreement', target: "to_do"},
                            {cond: (context) => context.option === 'disagreement', target: "ideas"},
                            { target: "others" }] 

                    },
                states: {
                    prompt: {
                        entry: send((context) => ({type: "SPEAK", value: `Cool.`})),
                    }
                }
            },
            others: {
                initial: "prompt",
                on: {
                    RECOGNISED: [
                        {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === true,
                        actions: assign((context) => { return { approval: true } }),
                        target: "created"},

                        {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === false,
                        actions: assign((context) => { return { approval: false } }),
                        target: "who"},

                        {cond: (context) => context.recResult === "help",
                        target: ".help" },

                    { target: ".nomatch" }
                ]
                },
                states: {
                    prompt: {
                        entry: send((context) => ({
                            type: "SPEAK",
                            value: `Do you want me to create an appointment with ${context.person} on ${context.day} at ${context.time}?`
                        })),
                        on: { ENDSPEECH: "ask" }
                    },
                    ask: {
                        entry: listen()
                    },
                    // help: {
                        // entry: say("We may be miscommunicating. Let's take a step back"),
                        // on: { ENDSPEECH: "#root.dm1.approval" }
                    // }
                }
            },
            // ...
            // others: {
            //     initial: "prompt",
            //     on: {
            //         ENDSPEECH: [
            //             '#root.dm1.idle']
            //         },
            //         states: {
            //             prompt: {
            //                 entry: send(({
            //                     type: "SPEAK",
            //                     value:"I suppose you said something strange" })),
            //                  on: { ENDSPEECH: '#root.dm1.positive.quest'}
            //                 }
            //             }
            // },
            // ...
            to_do: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                        '#root.dm1.idle']
                    },
                    states: {
                        prompt: {
                            entry: send(({
                                type: "SPEAK",
                                value:"These are the things you can choose to work on." })),
                             on: { ENDSPEECH: '#root.dm1.positive.quest'}
                            }
                        }

            },
            // ...
            ideas: {
                on: {
                    RECOGNISED: {
                        target: "#root.dm1.query1",
                        actions: assign((context) => { return { option: context.recResult } }),
                    }},
                ...promptAndAsk("How about working on your ideas?")
            }


        }})
