import { MachineConfig, send, assign } from "xstate";
import { listen, promptAndAsk, nluRequest, misUnderstood } from "./index";
// import { list } from "./todo_list"


export function Queries(ondone: string, onerror:string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
            invoke: {
                id: 'rasa',
                src: (context, event) => nluRequest(context.option),
                onDone: {
                    actions: [assign((context, event) => { return  {option: event.data.intent.name} }), 
                    (context: SDSContext, event: any) => console.log(event.data)],
                    target: ondone
                },
                onError: {
                    target: onerror,
                    actions: (context, event) => console.log(event.data)}
}})}

export const dmMachine1: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        // ...
        idle: {},
        // ...
        positive: {
            initial: "quest",
            states: {
                quest: {
                        on: {
                            RECOGNISED: {
                                target: '#root.dm1.query1',
                                actions: assign((context) => { return { option: context.recResult } }),
                            }},
                        ...promptAndAsk("Because you are in a good mood, I Would you like to do a chore?")
                    }
            // ...misUnderstood(`Would you like to do a chore?`,  ["#root.dm1.idle", "#root.initial_welcome.help"])
             }},
            query1: { 
                ...Queries('distributor1', "#root.initial_welcome.help")
            },
            distributor1: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                            {cond: (context) => context.option === 'agreement', target: "create_do"},
                            {cond: (context) => context.option === 'disagreement', target: "ideas"},
                            { target: "others" }] 

                    },
                states: {
                    prompt: {
                        entry: send((context) => ({type: "SPEAK", value: `Cool.`})),
                    }
                }
            },
            // ...
            others: {
                initial: "prompt",
                on: {
                    ENDSPEECH: [
                        '#root.dm1.idle']
                    },
                    states: {
                        prompt: {
                            entry: send(({
                                type: "SPEAK",
                                value:"Ummm, you said something strange" })),
                             on: { ENDSPEECH: '#root.dm1.positive.quest'}
                            }
                        }
            },
            // ...
            // to_do: {
            //     initial: "prompt",
            //     on: {
            //         ENDSPEECH: [
            //             '#root.dm1.idle']
            //         },
            //         states: {
            //             prompt: {
            //                 entry: send((context) => ({
            //                     type: "SPEAK",
            //                     value:`Now go and complete the following task: ${context}` })),
            //                  on: { ENDSPEECH: '#root.dm1.create_do'}
            //         }
            //     }
            // },
            //...
            create_do:{
                initial: "prompt",
                on: {
                    RECOGNISED: {
                        target: "to_do",
                        actions: assign((context) => { return { task: context.recResult } }),
                    }},
                    ...promptAndAsk("Tell me the things you have to accomplish.")
                },
            // ...
            to_do: {
                initial: "prompt",
                on: {
                    ENDSPEECH: ['#root.dm1.idle']
                    },
                    states: {
                        prompt: {
                            entry: send((context) => ({
                                type: "SPEAK",
                                value:`Now go and complete the following tasks: ${context.task}` })),
                             on: { ENDSPEECH: '#root.dm1.done_do'}
                    }
                }
            },
            // ...
            done_do: {
                initial: "prompt",
                on: {
                    ENDSPEECH: ['#root.dm1.idle']
                    },
                    states: {
                        prompt: {
                            entry: send((context) => ({
                                type: "SPEAK",
                                value:"Congratulations on your accomplishment! Here's a reward" })),
                             on: { ENDSPEECH: ['#root.dm1.idle', '#root.initial_welcome.goodbye']}
                    }
                }
            },
            // ...
            ideas: {
                on: {
                    RECOGNISED: {
                        target: "#root.dm1.query1",
                        actions: assign((context) => { return { option: context.recResult } }),
                    }},
                ...promptAndAsk("How about working on your ideas?")
            }
        }})


