import { MachineConfig, send, assign } from "xstate";
import {say, listen} from "./index";


export function misUnderstood(saythis: string, on_help:string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
        initial: 'prompt',
        states: {
        prompt: {
            entry: send((_context) => ({
                type: "SPEAK",
                value: (saythis)
            })),
            on: { ENDSPEECH: "ask" }
        },
        ask: {
            entry: listen()
        },
        nomatch: {
            entry: say("Sorry, I do not understand."),
            on: { ENDSPEECH: "prompt" }
        },
        help: {
            entry: say("We may be miscommunicating. Let's take a step back"),
            on: { ENDSPEECH: on_help }
        }
    }
})}

const grammar: { 
    [index: string]: {
        appointment?: string,
        to_do?: string,
        timer?: string,
        person?: string,
        day?: string,
        time?: string,
        approval?:  boolean,
    }} =  {
            "John": { person: "John Appleseed" },
            "Peter": { person: "Peter Horter" },
            "Jack": { person: "Jack Tomerson" },
            "Tom": { person: "Tom Peterson" },
            "Jill": { person: "Jill Panele" },
            "Jane": { person: "Jane Mayer" },
            "Anna": { person: "Anna Pana" },
            "Lora": { person: "Lora Cat" },

            "Monday": { day: "Monday" },
            "Tuesday": { day: "Tuesday" },
            "Wednesday": { day: "Wednesday" },
            "Thursday": { day: "Thursday" },
            "Friday": { day: "Friday" },
            "Saturday": { day: "Saturday" },
            "Sunday": { day: "Sunday" },

            "8": { time: "eight" },
            "9": { time: "nine" },
            "10": { time: "ten" },
            "11": { time: "eleven" },
            "at noon": { time: "twelve" },
            "12": { time: "twelve" },
            "1": { time: "thirteen" },
            "2": { time: "fourteen" },
            "3": { time: "fifteen" },
            "4": { time: "sixteen" },
            "5": { time: "seventeen" },
            "6": { time: "six" },
            "7": { time: "seven" },

            "of course": { approval: true },
            "yes": { approval: true },
            "yeah": { approval: true },
            "yup": { approval: true },
            "sure": { approval: true },
            "no": { approval: false },
            "nah": { approval: false },
            "nope": { approval: false }}


export const dmMachine1: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        // ...
        idle: {},
        // ...
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [
                    {cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"},

                    {cond: (context) => context.recResult === "help",
                    target: ".help" },

                    { target: ".nomatch" }]
            },
            ...misUnderstood(`Who are you meeting with?`, "#root.initial_welcome.help"),
        },
        // ...
        day: {
            initial: "prompt",
            on: {
                RECOGNISED: [
                    {cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "approval" },

                    {cond: (context) => context.recResult === "help",
                    target: ".help" },

                    { target: ".nomatch" } 
                ]
                },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} it is. On which day is your meeting?`
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I do not understand"),
                    on: { ENDSPEECH: "prompt" }
                },
                help: {
                    entry: say("We may be miscommunicating. Let's take a step back"),
                    on: { ENDSPEECH: "#root.dm1.who" }
                }

            },
            // ...misUnderstood(`OK. ${context.person} it is. On which day is your meeting?`, "#root.dm1.who"),
        },
        // ...
        approval: {
            initial: "prompt",
            on: {
                RECOGNISED: [
                    {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === true,
                    actions: assign((context) => { return { approval: true } }),
                    target: "summary_whole"},

                    {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === false,
                    actions: assign((context) => { return { approval: false} }),
                    target: "time"},

                    {cond: (context) => context.recResult === "help",
                    target: ".help" },

                    { target: ".nomatch" }
                ]
                },
                // ...misUnderstood(`Ok, meeting on ${context.day}. Will it take the whole day?`, "#root.dm1.day")},
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Ok, meeting on ${context.day}. Will it take the whole day?`
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I do not understand"),
                    on: { ENDSPEECH: "prompt" }
                },
                help: {
                    entry: say("We may be miscommunicating. Let's take a step back"),
                    on: { ENDSPEECH: "#root.dm1.day" }
                }
            }
        },
        // ...
        time: {
            initial: "prompt",
            on: {
                RECOGNISED: [
                    {cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "summary_time"},

                    {cond: (context) => context.recResult === "help",
                    target: ".help" },

                    { target: ".nomatch" }]
            },
        ...misUnderstood(`What time is your meeting?`, "#root.dm1.approval")},
        // ...
        summary_time: {
            initial: "prompt",
            on: {
                RECOGNISED: [
                    {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === true,
                    actions: assign((context) => { return { approval: true } }),
                    target: "created"},

                    {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === false,
                    actions: assign((context) => { return { approval: false } }),
                    target: "who"},

                    {cond: (context) => context.recResult === "help",
                    target: ".help" },

                { target: ".nomatch" }
            ]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} at ${context.time}?`
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I do not understand"),
                    on: { ENDSPEECH: "prompt" }
                },
                help: {
                    entry: say("We may be miscommunicating. Let's take a step back"),
                    on: { ENDSPEECH: "#root.dm1.approval" }
                }
            }
        },
        // ...
        summary_whole: {
            initial: "prompt",
            on: {
                RECOGNISED: [
                    {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === true,
                    actions: assign((context) => { return { approval: true } }),
                    target: "created"},

                    {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === false,
                    actions: assign((context) => { return { approval: false } }),
                    target: "who"},

                    {cond: (context) => context.recResult === "help",
                    target: ".help" },

                    { target: ".nomatch" }]
                },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} for the whole day?`})),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I do not understand"),
                    on: { ENDSPEECH: "prompt" }
                },
                help: {
                    entry: say("We may be miscommunicating. Let's take a step back"),
                    on: { ENDSPEECH: "#root.dm1.approval" }
                }
            }    
        },
        // ...
        created: { ...misUnderstood(`Your appoinment has been created.`, "#root.initial_welcome.help")
            // initial: "prompt",
            // states: {
            //     idle: {},
            //     prompt: {
            //         entry: send((context) => ({
            //             type: "SPEAK",
            //             value: `Your appoinment has been created.`})),
            //          on: { ENDSPEECH: "#root.initial_welcome.help" }
            //         }
            //     }    
            }
    }   
})
