import { MachineConfig, send, assign } from "xstate";
import { Queries, Conditional, grammar, promptAndAsk, promptHelpBye, say } from "./index";

export const dmMachine1: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        // ...
        idle: {},
        // ...
        positive: {
            initial: "quest1",
            states: {
                quest1: {
                    on: {
                        RECOGNISED: [
                                {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === true,
                                actions: assign((context) => { return { approval: true } }),
                                target: "#root.dm1.create_do"},

                                {cond: (context) => grammar[context.recResult] !== undefined && grammar[context.recResult].approval === false,
                                actions: assign((context) => { return { approval: false} }),
                                target: "#root.dm1.if_ideas"},

                                // {cond: (context) => context.option === 'help', target: '.help'}
                                ]
                        },
                        ...promptAndAsk("I suppose you could do something productive. Do you agree?")
                        },
                quest2: {
                    on: {
                        RECOGNISED: {

                            target: '#root.init.query3',
                            actions: assign((context) => { return { option: context.recResult } }),
                        }},
                        ...promptAndAsk("So what would you like to do?")
                    }
                }
            },
            // ....
            query3: {...Queries("#root.init.distributor", "#root.init.help")},


            // NOT USED
            // distributor: {
                // initial: "prompt",
                // on: {
                //     ENDSPEECH: [
                //             {cond: (context) => context.option === 'create', target: ['#root.dm2.idle',"#root.dm2.games"]},
                //             {cond: (context) => context.option === 'music', target: ['#root.dm2.idle',"#root.dm2.music"]},
                //             {cond: (context) => context.option === 'enterntain_me', target: ['#root.dm2.idle',"#root.dm2.entertain"]},
                //             {cond: (context) => context.option === 'games', target: ['#root.dm2.idle',"#root.dm2.games"]},
                //             {cond: (context) => context.option === 'music', target: ['#root.dm2.idle',"#root.dm2.music"]},
                //             {cond: (context) => context.option === 'enterntain_me', target: ['#root.dm2.idle',"#root.dm2.entertain"]},
                //             {cond: (context) => context.option === 'help', target: ['#root.dm2.idle',"#root.init.help"]},
                //             { target: "#root.dm1.distributor.others" }] 
                //     },
            //     states: {
            //         prompt: {
            //             entry: say("Understood."),
            //         },
            //         others: {
            //             entry: say("Ummm, you said something strange"),
            //             on: { ENDSPEECH: '#root.dm1.positive.quest1'}
            //             }
            //     }
            // },

            //...
            create_do:{
                initial: "prompt",
                on: {
                    RECOGNISED: {
                        actions: assign((context) => { return { task: context.recResult } }),
                        target: "to_do"}
                    },
                    ...promptAndAsk("Tell me the things you have to accomplish.")
                },
            // ...
            to_do: {
                initial: "prompt",
                states: {
                    prompt: {
                        entry: send((context) => ({
                            type: "SPEAK",
                            value:`Now go and complete the following tasks: ${context.task}` })),
                            on: { ENDSPEECH: '#root.dm1.annoy'}
                        }}
            },
            // ...
            annoy:{
                initial: "prompt",
                states: {
                        prompt: {
                            entry: say(`Have you done your task yet?`),
                            on: { ENDSPEECH: 'wait'}
                        },
                        wait: {
                            entry:[send('LISTEN'), send('WAIT', {delay: 3000, id: 'wait'})],
                            on: { WAIT: 'prompt'}
                        }
                    }
            },
            // ...
            if_ideas: {
                on: {
                    RECOGNISED: {
                        target: '#root.dm1.query2',
                        actions: assign((context) => { return { option: context.recResult } }),
                    }},
                ...promptAndAsk("Then how about working on your ideas?")
            },
            // ...
            query2: {...Queries('conditional2',"#root.init.help")},
            conditional2: {...Conditional('agreement', "create_ideas", 'disagreement', "#root.dm1.positive.quest2", "#root.dm1.done.others", '#root.dm2.idle', `Cool.`)},
            // ...
            create_ideas:{
                initial: "prompt",
                on: {
                    RECOGNISED: {
                        target: "idea",
                        actions: assign((context) => { return { idea: context.recResult } }),
                    }},
                    ...promptAndAsk("Tell me your idea.")
                },
            // ...
            idea: {
                initial: "prompt",
                // on: {ENDSPEECH: ['#root.dm1.idle']},
                    states: {
                        prompt: {
                            entry: send((context) => ({
                                type: "SPEAK",
                                value:`You should work on your idea of ${context.idea}. Let me play some music while you are at it.` })),
                            on: { ENDSPEECH: '#root.dm1.done'}
                    }
                }
            },
            // ...
            done: {
                initial: "prompt",
                // on: {ENDSPEECH: ['#root.dm1.idle']},
                    states: {
                        prompt: {
                            entry: say("Congratulations on your accomplishment! Here's a reward"),
                            on: { ENDSPEECH: ['#root.dm1.idle', '#root.init.help.goodbye']}
                    }, 
                        others: {
                            entry: say("Ummm, you said something strange"),
                            on: { ENDSPEECH: '#root.dm1.positive.quest2'}
                            }
                    }
                }         
}})
