import { MachineConfig } from "xstate";
import {say, listen} from "./index";

export const dmMachine2: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        idle: {},
        denial: {
            initial: "denial_message",
            // on: { ENDSPEECH: "#root.initial_welcome.help.prompt"},
            states: {
                denial_message: {
                    entry: say("Welcome to Timer. It will be improved later."),
                    on: { ENDSPEECH: "#root.dm2.idle" }                },
        }
    }

}})
