import { MachineConfig } from "xstate";
import {say, listen } from "./index";

export const dmMachine3: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    on: { ENDSPEECH: "#root.initial_welcome.help"},
    states: {

        idle: {},
        denial: {
            initial: "denial_message",
            // on: { ENDSPEECH: "#root.initial_welcome.help"},
            states: {
                denial_message: {
                    entry: say("Welcome to To-do. It will be improved later."),
                    on: { ENDSPEECH: "#root.initial_welcome.goodbye" },
                    // always: "#root.dm3.idle"
                }
        },
        // always: "idle"
    }

}})
