import { MachineConfig, send, assign } from "xstate";
import {say, listen, promptAndAsk, nluRequest, misUnderstood } from "./index";
import { list } from "./todo_list"



export function Queries(ondone: string, onerror:string, option1:string, target1:string, option2:string, 
    target2:string, option3:string, target3:string, othertarget:string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
            invoke: {
                id: 'rasa',
                src: (context, event) => nluRequest(context.option),
                onDone: {
                    actions: [assign((context, event) => { return  {option: event.data.intent.name} }), 
                    (context: SDSContext, event: any) => console.log(event.data)],
                    target: ondone
                },
                onError: {
                    target: onerror,
                    actions: (context, event) => console.log(event.data)}
            },
            initial: "prompt",
            on: {
                ENDSPEECH: [
                    {cond: (context) => context.option === option1, target: target1},
                    {cond: (context) => context.option === option2, target: target2},
                    {cond: (context) => context.option === option3, target: target3},
                    { target: othertarget }]

                },
            states: {
                prompt: {
                    entry: send((context) => ({type: "SPEAK", value: `Ok.`})),
                }
        }})}




