import { MachineConfig } from "xstate";
// import { Endings } from "./dmAppointment";
import {say } from "./index";

export const dmMachine2: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
//         idle: {},
//         denial: {...Endings("Welcome to Timer. It will be improved later.","#root.initial_welcome.goodbye")},
//         }
        idle: {},
        denial: {
            initial: "denial_message",
            // on: { ENDSPEECH: "#root.initial_welcome.help"},
            states: {
                denial_message: {
                    entry: say("Welcome to To-do. It will be improved later."),
                    on: { ENDSPEECH: "#root.initial_welcome.goodbye" },
                    // always: "#root.dm3.idle"
                }
        },
        // always: "idle"
    }

}})
// })
