import { MachineConfig, send, assign } from "xstate";
import {say, listen, promptAndAsk } from "./index";
import { loadGrammar } from './runparser'
import { parse } from './chartparser'
import { grammar } from './grammars/orderGrammar'

const gram = loadGrammar(grammar)

export const Order = (input: string) => {
    const gram = loadGrammar(grammar);
    const prs = parse(input.split(/\s+/), gram);
    const result = prs.resultsForRule(gram.$root)[0]
    const reply = "action: "+result.command.action+", object:" + result.command.object;
    return reply
}

export const dmMachine4: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'idle',
    states: {
        idle: {},
        welcome: {
            initial: "prompt",
            on: { ENDSPEECH: "task" },
            states: {
                prompt: { entry: say("Welcome to Smart Home") }
            }
        },
        task: {
            initial: "prompt",
            on: {
                RECOGNISED: {
                    target: 'perform',

                    actions: assign((context) => {return { order: Order(context.recResult) } })
                        }
                    },
            ...promptAndAsk("What would you like me to do?")
        },
        perform: {
            initial: "prompt",
            states: {
                prompt: { 
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK ${context.order}.`
                    }))
                }
            }
        }
    }
})
